import React from 'react';
import Sound from 'react-sound';
import texts from '../../texts.json';
import './ListenedSongs.css';

const ListenedSongs = (props) => {

    return (
        <div>
            <h4 className="instruct">{texts[props.language].listenedSongs}</h4>
            <ul id="mistakes" className="instruct">  
                {props.unknownSongs.map((song) => {
                const url= song.uri
                return (
                    <li key={song.uri} className="mistake-list">
                        <div className="song-name">
                        {song.name} 
                        </div>
                        <a href={url}>{song.uri}</a>
                        <button className="repeat-button" onClick={props.onClick ? () => this.stopMusic() : () => this.getSongUrl(song)}>
                        {this.state.playing ? texts[this.props.language].pauseText : texts[this.props.language].listenAgain} 
                        </button> 
                    </li>
                )
                })}
            </ul>
            <Sound 
                url={props.songUrl}
                playStatus={props.playerState}
                autoLoad={props.autoLoad}
            />
        </div>
    )
};

export default ListenedSongs;
