import React, {Component} from 'react';
import { Link } from 'react-router-dom';
import '../../App.css';
import './Home.css';
import logo from '../../Pictures/logo_la_pegatina_2018.png';

class  Home extends Component {

  state = {
    selectedLanguage: "español"
  }

  setLanguageCatalan = () => {
    this.setState ({
      selectedLanguage: "catalan"
    })
  }

  render () {
    return (
      <div className="container">
      <div class="language-dropdown">
        <button class="dropbtn">SELECCIONA UN IDIOMA</button>
        <div class="dropdown-content">
          <img src="../../Pictures/bandera_catala_small.png" onClick={() => this.setLanguageCatalan()} alt="catalan"/>
          <a href="#">Link 2</a>
          <a href="#">Link 3</a>
        </div>
      </div>
      <div className="main">
        <h3 className="hideGame"><Link to="/">Home</Link></h3>

        <p className="playWith">Juega con</p>
        <img src={logo} alt="" className="laPegatina" />

        <Link className="btn-game" to="game">Juega con La Pegatina!</Link>
        <a className="btn-game" href="https://playwith.es">Juega con música de todo el mundo</a>
      </div>
    </div>
    )
  }
}


export default Home;
