import React, { useState } from 'react';

const ButtonIgRoundOne = (props) => {

    const [hasBeenClicked, makeButtonClicked] = useState(false)

    var colorClass = 'colorClass'

    const ANSWERS_TIME_DISPLAYED = 5

    const checkRightOrWrong = () => {

        makeButtonClicked(true)

        if (props.value === props.currentTags) {
            props.addToCounter();    
        }

        setTimeout(() => {

            // props.setRandomImageAndTags()

        }, ANSWERS_TIME_DISPLAYED * 1000);
    }

    if (props.value === props.currentTags) {
        colorClass = 'green'
    } else if (hasBeenClicked === true) {
        colorClass = 'red'
    } else if (hasBeenClicked === false) {
        colorClass = 'gray'
    }

    return (
        <button className={"instagram-game-button " + colorClass} onClick={checkRightOrWrong}>
            {props.value}
        </button>
    )
};

export default ButtonIgRoundOne;
