import React, {Component} from 'react';
import Shuffle from '../Utils/Shuffle'
import ButtonIG from './Button_Ig'

class Instagram extends Component {

    state= {
        images: [],
        onlyImagesArr: [],
        imageCounter: 0,
        locationsArr: [],
        currentLocation: "",
        chosenLocation: "",
    }



    async componentDidMount() {

        const response = await fetch('https://www.instagram.com/bestvacations/?__a=1');

        const data = await response.json();

        console.log("FROM the API", data.graphql);

        this.setState({
            images: data.graphql.user.edge_owner_to_timeline_media.edges
        })

        const imagesWithLocationArr = data.graphql.user.edge_owner_to_timeline_media.edges.filter(img => img.node.location !== null)

        this.setState ({
            locationsArr: imagesWithLocationArr.map((image) => image.node.location.name)
        })

        console.log("Locations Array", this.state.locationsArr)

        this.getPhotoToDisplay();
    };

    getPhotoToDisplay = () => {

        this.setState({
            onlyImagesArr: Shuffle(this.state.images.map((image) => image.node.thumbnail_resources[4].src))
        })
    }

    setNewRandomPhoto = () => {

        this.setState({
            imageCounter: this.state.imageCounter+1,
        })
    }

    writeChosenLocation= (location) => {
        this.setState ({
            chosenLocation: location
        })
      }

    render () {

        const { onlyImagesArr, imageCounter } = this.state;

        return (
            <div>
                {/* { imageCounter <= onlyImagesArr.length-1

                    ? (<div>
                        <img src={onlyImagesArr[imageCounter]} alt="" />
                        <button onClick={this.setNewRandomPhoto}>Siguiente</button>
                    </div>)

                    : (<div>
                        <p>No hay más imágenes para mostrar, vuelve pronto</p>
                    </div>
                    )
                } */}

                <img src={onlyImagesArr[imageCounter]} alt="" />

                {this.state.locationsArr.map((location) => {
                    return (
                    <ButtonIG 
                        key={imageCounter} 
                        printedLocation={location} 
                        onClick={() => this.writeChosenLocation(location)}
                        currentLocation={this.state.currentLocation}
                    />
                    )
                })}
            </div>
        )
    }

}

export default Instagram;
