import React from 'react';
import './App.css';
import {Switch, Route} from 'react-router-dom';
import Game from './Components/Game/Game';
import Home from './Components/Home/Home';
import Instagram from './Components/Instagram/Instagram'
import SocialMedia from './Components/SocialMedia/SocialMedia';
import Team from './Components/Team/Team';
import catalan from './Pictures/bandera_catalan_small.png';
import spanish from './Pictures/bandera_spanish_small.png';
import english from './Pictures/bandera_english_small.png';
import french from './Pictures/bandera_french_small.png';


class App extends React.Component {

  state = {
    selectedLanguage: "spanish",
  }

  languagesAvailableArr = [catalan, spanish, english, french]

  setLanguage = (lang) => {
    this.setState ({
      selectedLanguage: lang
    })
  }

  render() {
    return ( 
      <div>
        <div className="dropdown">
          <button className="dropbtn">Elige tu idiome</button>
          <div className="dropdown-content">
            {this.languagesAvailableArr.map((lang) => 
              <img src={`./Pictures/bandera_${lang}_small.png`} onClick={() => this.setLanguage(lang)} alt=""/>
            )}
          </div>
        </div>
        <Switch>
          <Route exact path='/' render={props => <Home {...props} />} />
          <Route path='/game' render={props => <Game {...props} />} />   
          <Route path='/team' render={props => <Team {...props} />} />
          <Route path='/instagram' render={props => <Instagram {...props} />} />
        </Switch> 
        <div className="social-media-follow-buttons">
          <SocialMedia />
        </div>
      </div>
    );
  }
}

export default App;
