import React from 'react';
import './Button_Ig.css';

const ButtonIG = (props) => {

    function checkRightOrWrong (event) {
        if (event.props.value === event.props.randomImageLocation) {
            event.props.counter = event.props.counter+1;
            event.props.setRandomImageAndLocations();
        } else {
            event.props.setRandomImageAndLocations();
        }
    }

    return (
        <button onClick={()=> checkRightOrWrong()}>
            {props.value}
        </button>
    )
};

export default ButtonIG;
