import React from 'react';
import './App.css';
import {Switch, Route} from 'react-router-dom';
import Game from './Components/Game/Game';
import Home from './Components/Home/Home';
import Instagram from './Components/Instagram/Instagram'
import SocialMedia from './Components/SocialMedia/SocialMedia';
import Team from './Components/Team/Team';
import catalan from './Pictures/bandera_catala_small.png';
import spanish from './Pictures/bandera_espana_small.png';
import english from './Pictures/bandera_england_small.png';
import french from './Pictures/bandera_france_small.png';


//packages used: react router, react sound, leaflet, leaflet react, 

class App extends React.Component {

  state = {
    selectedLanguage: "spanish"
  }

  setLanguageCatalan = () => {
    this.setState ({
      selectedLanguage: "catalan"
    })
  }

  setLanguageSpanish = () => {
    this.setState ({
      selectedLanguage: "spanish"
    })
  }

  setLanguageEnglish = () => {
    this.setState ({
      selectedLanguage: "english"
    })
  }

  setLanguageFrench = () => {
    this.setState ({
      selectedLanguage: "french"
    })
  }

  render() {
    return ( 
      <div>
        {/* <div class="language-dropdown">
          <button class="dropbtn">Selecciona un idioma</button>
          <div class="dropdown-content">
            <img src={spanish} onClick={() => this.setLanguageSpanish()} alt="spanish"/>
            <img src={catalan} onClick={() => this.setLanguageCatalan()} alt="catalan"/>
            <img src={english} onClick={() => this.setLanguageEnglish()} alt="english"/>
            <img src={french} onClick={() => this.setLanguageFrench()} alt="french"/>
          </div>
        </div> */}
        <Switch>
          <Route exact path='/' render={props => <Home {...props} />} />
          <Route path='/game' render={props => <Game {...props} />} />   
          <Route path='/team' render={props => <Team {...props} />} />
          <Route path='/instagram' render={props => <Instagram {...props} />} />
        </Switch> 
        <div className="social-media-follow-buttons">
          <SocialMedia />
        </div>
      </div>
    );
  }
}

export default App;
