import React from 'react';
import './Button.css';

const Button = (props) => {

    const isCorrect = props.printedSong === props.currentSong;

    var hasBeenClicked = false

    const onClickHandler = () => {
        // Faig coses "internes"
        hasBeenClicked = true

        props.onClick()
    }

    return (
        <button 
            onClick={onClickHandler} 
            className={"myButton button " + (isCorrect & hasBeenClicked ? "green" : "red")}>
                {props.printedSong}
        </button>
    )
};


export default Button;
