import React, {Component} from 'react';

class Instagram extends Component {

    state= {
        data: "",
    }

    async componentDidMount() {
        // const response = fetch(`https://www.instagram.com/lapegatina/?__a=1`)
        //   .then(response => response.json())
        //   .then (result => {
        //     this.setState({
        //       data: result,
        //     })
        //     console.log("PACO", result)
        //   })

        const response = await fetch('https://www.instagram.com/lapegatina/?__a=1');

        const data = await response.json();

        console.log(data.graphql);

        this.setState({
            data: data.graphql.user
        })
    };

    render () {
        return (
            <div>
                {/* {this.state.data.edge_owner_to_timeline_media.edges.map(function (edge) {return edge.node.media_preview} )} */}
                <img src={this.state.data.edge_owner_to_timeline_media.edges[0].node.media_preview} alt=""></img>
                <div>
                    <p>{this.state.data.biography}</p>
                </div>
            </div>
        )
    }

}

export default Instagram;
