        // this.setState ({
        //     imagesWithLocationArrOfObj,
        // })

        // this.getPhotosToDisplay();
    };




    render () {

        const { imageCounter, fourImagesArr, fourLocationsToPrintInTheButtonArr } = this.state;

        console.log(fourLocationsToPrintInTheButtonArr)

        return (
            <div>

                <img src={fourImagesArr[imageCounter]} alt="" />

                {fourLocationsToPrintInTheButtonArr.map((location) => {
                    return (
                        <button>{location}</button>
                    )
                })}
            </div>
        )
    }

}

export default Instagram;
