import React, {Component} from 'react';
import Shuffle from '../Utils/Shuffle'
import ButtonIG from './Button_Ig';

class Instagram extends Component {

    state= {
        imagesFromAPIArrOfObj: [], //Array of objects retrieved from the API. Each object is for a different image but contains different keys
        imagesWithLocationArrOfObj: [], //imagesFromAPIArrOfObj but only the ones that have .location. Still an array of objects
        onlyImagesArr: [], //Array of images, JUST images, no other keys, retrieved from the API and shuffled
        fourImagesArr: [],
        imageCounter: 0, //Index from the onlyImagesArray of the image displayed
        allPicturesLocationsArr: [], //Array with just the NAMES of the locations of ALL images retrieved from the API that DO have a location
        fourLocationsToPrintInTheButtonArr: [],
    }

    getPhotosToDisplay = () => { 

        this.setState({
            onlyImagesArr: Shuffle(this.state.images.map((image) => image.node.thumbnail_resources[4].src))
        })

        this.getFourPhotosToDisplay();
    }

    getFourPhotosToDisplay = () => {

        let fourPhotosToDisplayArr = this.state.onlyImagesArr.slice(0, 4);

        this.setState({
            fourImagesArr: fourPhotosToDisplayArr
        })
    }

    getFourLocationsToPrintInTheButtons = () => {
        let fourLocationsToPrintInTheButtonArr = this.state.allPicturesLocationsArr.slice(0, 4);

        this.setState({
            fourLocationsToPrintInTheButtonArr
        })
    }




    async componentDidMount() {

        const response = await fetch('https://www.instagram.com/bestvacations/?__a=1');

        const data = await response.json();

        console.log("FROM the API", data.graphql);

        this.setState({
            imagesFromAPIArrOfObj: data.graphql.user.edge_owner_to_timeline_media.edges
        })

        const imagesWithLocationArrOfObj = data.graphql.user.edge_owner_to_timeline_media.edges.filter(img => img.node.location !== null)

        this.setState ({
            imagesWithLocationArrOfObj,
            locationsArr: imagesWithLocationArrOfObj.map((image) => image.node.location.name)
        })

        console.log("Locations Array", this.state.locationsArr)

        this.getPhotosToDisplay();
    };




    render () {

        const { onlyImagesArr, imageCounter, fourImagesArr } = this.state;

        return (
            <div>

                <img src={fourImagesArr[imageCounter]} alt="" />

                {this.state.fourLocationsToPrintInTheButton.map((location) => {
                    return (
                        <button>{location}</button>
                    )
                })}
            </div>
        )
    }

}

export default Instagram;
