import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import '../../App.css';
import './Home.css';
import logo from '../../Pictures/logo_la_pegatina_2018.png';
import Navbar from '../Navbar/Navbar';
import texts from '../../texts.json';

// const printName = ({ name }) => {
//     // const { name } = user;
//     // const name = user.name
//     console.log(name);
// };


class Home extends Component {

    state={
        selectedLanguage: 'spanish'

    }

    setLanguage = (lang) => {
        this.setState({
            selectedLanguage: lang,
        });
    }

    render() {

        const { selectedLanguage } = this.state;

        return (
            <div>
                <div className="container">
                    <div className="main">
                        <Navbar onChangeLanguage={this.setLanguage} />
                        <h3 className="hideGame"><Link to="/">Home</Link></h3>
                        <p className="playWith">{texts[selectedLanguage].playwithTitle}</p>
                        <img src={logo} alt="la pegatina logo" className="laPegatina" />
                        <div className="home-play-buttons">
                            <Link className="btn-game" to="spotifyroundone">{texts[selectedLanguage].spotifyPlayWithButton}</Link>
                            <Link className="btn-game" to="youtuberoundone">{texts[selectedLanguage].youtubePlayWithButton}</Link>
                            <Link className="btn-game" to="instagramroundone">{texts[selectedLanguage].instagramPlayWithButton}</Link>
                            <a className="btn-game" href="https://playwith.es">{texts[selectedLanguage].allmusicButton}</a>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Home;
